function get_post_for($title, $type = 'page'){
  global $wpdb;
  
  $sql = $wpdb->prepare("SELECT * FROM {$wpdb->posts} AS p WHERE p.post_type=%s AND p.post_status='publish' AND p.post_title=%s", $type, $title);
  $page = $wpdb->get_row($sql);
  if(!empty($page)){
    $sql = $wpdb->prepare("SELECT * FROM {$wpdb->posts} AS p WHERE p.ID=%d", $page->ID);
    $page = $wpdb->get_row($sql);
  }
  
  return $page;
}

function get_post_name_for($name, $type = 'page'){
  global $wpdb;
  
  $sql = $wpdb->prepare("SELECT * FROM {$wpdb->posts} p WHERE p.post_type=%s AND p.post_status='publish' AND p.post_name=%s", $type, $name);
  $page = $wpdb->get_row($sql);
  if(!empty($page)){
    $sql = $wpdb->prepare("SELECT * FROM {$wpdb->posts} p WHERE p.ID=%d", $page->ID);
    $page = $wpdb->get_row($sql);
  }
  
  return $page;
}

// OPTION
function pwsq_option($opt, $echo = true, $with_permalink = false){
  global $shortname;
  
  if($echo){
    if($with_permalink){ echo get_permalink(get_option($shortname . '_' . $opt)); return; }
    echo get_option($shortname . '_' . $opt);
  }
  else{
    if($with_permalink){ return get_permalink(get_option($shortname . '_' . $opt)); }
    return get_option($shortname . '_' . $opt);
  }
}


