function pwsq_admin_menu() {
  global $plugin_setting_name, $shortname, $options;

  if ( isset($_GET['page']) && isset($_GET['post_type']) && $_GET['page'] == 'personality-test-setting' && $_GET['post_type'] == 'personality-test' ) {
    if ( 'save' == @$_REQUEST['action'] ) {
      foreach ($options as $value) {
        if(isset($value['id'])) update_option( $value['id'], $_POST[ $value['id'] ] );
      }
      $_GET['saved'] = true;
    }else if( 'reset' == @$_REQUEST['action'] ) {
      foreach ($options as $value) {
        delete_option( $value['id'] );
      }
      header("Location: admin.php?page=functions.php&reset=true");
      die;
    }
  }
  add_submenu_page( 'edit.php?post_type=personality-test', 'Personality Test Setting', 'Setting', 'manage_options', 'personality-test-setting', 'personality_test_setting' );
}

