function isAjax ()
{
  return isset($_SERVER['HTTP_X_REQUESTED_WITH']) && $_SERVER['HTTP_X_REQUESTED_WITH'] == "XMLHttpRequest";
}

add_filter( 'posts_where', 'wp_query_title_custom_filter1', 10, 2 );
function wp_query_title_custom_filter1( $where, &$wp_query )
{
    global $wpdb;
    if ( $title_custom_filter = $wp_query->get( 'search_title' ) ) {
        $where .= ' AND ' . $wpdb->posts . '.post_title LIKE \'' . esc_sql( like_escape( $title_custom_filter ) ) . '%\'';
    }
    return $where;
}

