add_action('save_post', 'post_save_details2');
function post_save_details2(){
  global $post;
 
  if(isset($post->post_type) && $post->post_type == 'personality-test'){
    // remove all previous meta first
    delete_post_meta($post->ID, "_pt_meta_qa_list");

    $qa_list = array();
    if($_POST && $_POST['qa'] && $_POST['qa']['question'] && is_array($_POST['qa'])){
      // create associative array
      foreach($_POST['qa']['question'] as $idx => $question) {
        if(empty($question)){ continue; }
        array_push($qa_list, array(
            'question' => $question,
            // RIASEC
            'a_r' => @$_POST['qa']['a_r'][$idx],
            'a_i' => @$_POST['qa']['a_i'][$idx],
            'a_a' => @$_POST['qa']['a_a'][$idx],
            'a_s' => @$_POST['qa']['a_s'][$idx],
            'a_e' => @$_POST['qa']['a_e'][$idx],
            'a_c' => @$_POST['qa']['a_c'][$idx]
          ));
      }
    }
    if(!empty($qa_list)){
      // $qa_list = serialize($qa_list);
      update_post_meta($post->ID, "_pt_meta_qa_list", $qa_list);  
    }
  }
}

