function pwsq_sc($atts){
  extract(shortcode_atts(array(
    'test' => ''
  ), $atts));

  if(!empty($test)){
    $pt = get_post_name_for($test, 'personality-test');
    if($pt){
      return render_test_session($test);
    }
  }
}

add_shortcode('pwsq', 'pwsq_sc');
